sealed class CryptoException(message: String): kotlin.Exception(message) {
        
        class InvalidSecretKey(message: String) : CryptoException(message)
        
        class InvalidPublicKey(message: String) : CryptoException(message)
        
        class InvalidEphemeralKey(message: String) : CryptoException(message)
        
        class EcdhFailed(message: String) : CryptoException(message)
        
        class EncryptionFailed(message: String) : CryptoException(message)
        
        class DecryptionFailed(message: String) : CryptoException(message)
        
        class HexException(message: String) : CryptoException(message)
        
        class InvalidNonce(message: String) : CryptoException(message)
        
        class InvalidCiphertext(message: String) : CryptoException(message)
        
        class KeyDerivationFailed(message: String) : CryptoException(message)
        
        class SignatureVerificationFailed(message: String) : CryptoException(message)
        
        class InvalidNsec(message: String) : CryptoException(message)
        
        class InvalidNpub(message: String) : CryptoException(message)
        
        class JsonException(message: String) : CryptoException(message)
        
        class WrongPin(message: String) : CryptoException(message)
        
        class InvalidPin(message: String) : CryptoException(message)
        

    companion object ErrorHandler : UniffiRustCallStatusErrorHandler<CryptoException> {
        override fun lift(error_buf: RustBuffer.ByValue): CryptoException = FfiConverterTypeCryptoError.lift(error_buf)
    }
}

/**
 * @suppress
 */
public object FfiConverterTypeCryptoError : FfiConverterRustBuffer<CryptoException> {
    override fun read(buf: ByteBuffer): CryptoException {
        
            return when(buf.getInt()) {
            1 -> CryptoException.InvalidSecretKey(FfiConverterString.read(buf))
            2 -> CryptoException.InvalidPublicKey(FfiConverterString.read(buf))
            3 -> CryptoException.InvalidEphemeralKey(FfiConverterString.read(buf))
            4 -> CryptoException.EcdhFailed(FfiConverterString.read(buf))
            5 -> CryptoException.EncryptionFailed(FfiConverterString.read(buf))
            6 -> CryptoException.DecryptionFailed(FfiConverterString.read(buf))
            7 -> CryptoException.HexException(FfiConverterString.read(buf))
            8 -> CryptoException.InvalidNonce(FfiConverterString.read(buf))
            9 -> CryptoException.InvalidCiphertext(FfiConverterString.read(buf))
            10 -> CryptoException.KeyDerivationFailed(FfiConverterString.read(buf))
            11 -> CryptoException.SignatureVerificationFailed(FfiConverterString.read(buf))
            12 -> CryptoException.InvalidNsec(FfiConverterString.read(buf))
            13 -> CryptoException.InvalidNpub(FfiConverterString.read(buf))
            14 -> CryptoException.JsonException(FfiConverterString.read(buf))
            15 -> CryptoException.WrongPin(FfiConverterString.read(buf))
            16 -> CryptoException.InvalidPin(FfiConverterString.read(buf))
            else -> throw RuntimeException("invalid error enum value, something is very wrong!!")
        }
        
    }

    override fun allocationSize(value: CryptoException): ULong {
        return 4UL
    }

    override fun write(value: CryptoException, buf: ByteBuffer) {
        when(value) {
            is CryptoException.InvalidSecretKey -> {
                buf.putInt(1)
                Unit
            }
            is CryptoException.InvalidPublicKey -> {
                buf.putInt(2)
                Unit
            }
            is CryptoException.InvalidEphemeralKey -> {
                buf.putInt(3)
                Unit
            }
            is CryptoException.EcdhFailed -> {
                buf.putInt(4)
                Unit
            }
            is CryptoException.EncryptionFailed -> {
                buf.putInt(5)
                Unit
            }
            is CryptoException.DecryptionFailed -> {
                buf.putInt(6)
                Unit
            }
            is CryptoException.HexException -> {
                buf.putInt(7)
                Unit
            }
            is CryptoException.InvalidNonce -> {
                buf.putInt(8)
                Unit
            }
            is CryptoException.InvalidCiphertext -> {
                buf.putInt(9)
                Unit
            }
            is CryptoException.KeyDerivationFailed -> {
                buf.putInt(10)
                Unit
            }
            is CryptoException.SignatureVerificationFailed -> {
                buf.putInt(11)
                Unit
            }
            is CryptoException.InvalidNsec -> {
                buf.putInt(12)
                Unit
            }
            is CryptoException.InvalidNpub -> {
                buf.putInt(13)
                Unit
            }
            is CryptoException.JsonException -> {
                buf.putInt(14)
                Unit
            }
            is CryptoException.WrongPin -> {
                buf.putInt(15)
                Unit
            }
            is CryptoException.InvalidPin -> {
                buf.putInt(16)
                Unit
            }
        }.let { /* this makes the `when` an expression, which ensures it is exhaustive */ }
    }

}




